//API 통신과 같은 작업을 하는 액션 메서드(action creator)를 모아 둔 폴더다.

//action type
const SET_TOKEN = 'SET_TOKEN'
const GET_TOKEN = 'GET_TOKEN'

// redux가 가지고있는 토큰을 넘겨줍니다.
function get_token(){
    return{
        type: GET_TOKEN,
        payload: token
    }
}

// 로컬이 가지고있는 토큰을 받아옵니다.
function set_token(token){
    return{
        type: SET_TOKEN,
        payload: token
    }
}

export {
    SET_TOKEN,
    GET_TOKEN,
    get_token,
    set_token
}
