import { StyleSheet } from 'react-native';

// 중앙에 컨텐츠들 모우는 기본 셋팅입니다.
const defaultStyle = {
    flex: 1,
    justifyContent: "center",
    alignItems: "center"
}

// 기본 폰트 스타일입니다.
const defaultFont = {
    fontSize: 18,
    color: '#3c444f',
    fontWeight: '700'

}

//기본 box 스타일입니다.
const defaultBox = {
    padding: 30,
    borderRadius: 20,
    backgroundColor: 'white'
}

export const authstyles = StyleSheet.create({
    defaultStyle,
    defaultFont,

})
export const loading = StyleSheet.create({
    defaultStyle,
    defaultFont,
    blackWall:{
    position: "absolute", 
    opacity: 0.5, 
    backgroundColor: "black", 
    width: "100%", 
    height: "100%"
  }  
})
export const homestyles = StyleSheet.create({
    defaultStyle,
    defaultBox,
    defaultFont,
    emptybutton:{
        margin: 15,
        width: "30%",
        alignItems: 'center'
    },
    teambutton:{
        marginBottom: 20,
        width: "90%"
    },
    homeFont:{
        fontSize: 15,
    }

})
export const deadlinestyles = StyleSheet.create({
    defaultFont,
    imageButton:{
        width: 120,
        height: 120,
    },
    image:{
        backgroundColor: "black",
        height: '100%',
        width: '100%',
    },
    barcodeText:{
        fontSize: Platform.OS === 'ios' ? 15 : 13,
        color: "#808080",
        marginTop: 5,
        fontWeight: '700'
    },
    confirmButton:{
        alignItems: 'center',
        backgroundColor: '#3c444f',
        padding: 20,
    },
    confirmText:{
        color: '#fff',
        fontSize: 13
    }
})
export const modalstyles = StyleSheet.create({
    defaultStyle,
    defaultBox,
    defaultFont,
    // 설명 스타일
    exMessage:{
        color: "#808080", 
        fontSize: 12,
        fontWeight: 
        'bold', 
        padding: 5
    },
    // 텍스트 입력 스타일
    TextInput:{
        marginTop: 10,
        height: 30, 
        width: 180,
        borderBottomWidth: 1,
        borderBottomColor: '#808080'
    },
    barcodeButton:{
    marginLeft: 25,
    marginRight: 25,
    marginTop: 15,
    width: 90,
    padding: 12,
    borderRadius: 20
    },
    footButton:{
    marginLeft: 25,
    marginRight: 25,
    marginTop: 30,
    width: 90,
    padding: 12,
    borderRadius: 20
    },
    listviewButton:{
        borderRadius: 20, 
        width: 90, 
        backgroundColor: '#3c444f', 
        alignItems: 'center', 
        marginTop: 20, 
        padding: 12
    },
    cancleButton:{
    borderColor: "#adb5bd", 
    borderWidth: 1
    },
    cancleText:{
    color: "#adb5bd", 
    fontSize: 13, 
    fontWeight: "bold",
    textAlign: "center"
    },
    confirmButton:{
    backgroundColor: '#3c444f'
    },
    confirmText:{
    color: "white", 
    fontSize: 13, 
    fontWeight: "bold",
    textAlign: "center"
    }, 


})
